mapper.insert:
insert
  into t_board
  (
    tboard_no
  , user_no	
  , thumbnail
  , tboard_title
  , tboard_content
  , price
  , tag
  )
  values
  (
  	'T' || SEQ_TBNO.NEXTVAL
  , #{userNo}
  , #{thumbnail}
  , #{tboardTitle}
  , #{tboardContent}
  , #{price}
  , #{tag}
  )

mapper.update:
update
	   t_board
   set tboard_title = #{tboardTitle}
     , tboard_content = #{tboardContent}
     , price = #{price}
     , tag = #{tag}
     , thumbnail = #{thumbnail}
 where tboard_no = #{tboardNo}

mapper.insert:
insert
  into attachment
  (
  	file_no
  , ref_no
  , origin_name
  , change_name
  , file_path
  )
  values
  (
  	SEQ_FNO.NEXTVAL
  , #{refNo}
  , #{originName}
  , #{changeName}
  , #{filePath}
  )
