mapper.insert:
insert
  into t_board
  (
    tboard_no
  , user_no	
  , thumbnail
  , tboard_title
  , tboard_content
  , price
  , tag
  )
  values
  (
  	'T' || SEQ_TBNO.NEXTVAL
  , #{userNo}
  , #{thumbnail}
  , #{tboardTitle}
  , #{tboardContent}
  , #{price}
  , #{tag}
  )

mapper.update:
update
	   t_board
   set tboard_title = #{tboardTitle}
     , tboard_content = #{tboardContent}
     , price = #{price}
     , tag = #{tag}
     , thumbnail = #{thumbnail}
 where tboard_no = #{tboardNo}

mapper.insert:
insert
  into attachment
  (
  	file_no
  , ref_no
  , origin_name
  , change_name
  , file_path
  )
  values
  (
  	SEQ_FNO.NEXTVAL
  , #{refNo}
  , #{originName}
  , #{changeName}
  , #{filePath}
  )

insert
  into reply
  (
    reply_no
  , board_no
  , reply_writer
  , reply_content
  )
  values
  (
    SEQ_RPL.NEXTVAL
  , #{boardNo}
  , #{replyWriter}
  , #{replyContent}
  )

mapper.update:
update
       board
   set board_title = #{boardTitle}
     , board_content = #{boardContent}
 where board_no = 'B' || #{boardNo}
   and status = 'Y'

update
       board
   set board_title = #{boardTitle}
     , board_content = #{boardContent}
 where board_no = 'B' || #{boardNo}
   and status = 'Y'

update
       board
   set status = 'N'
 where board_no = #{boardNo}
   and board_writer = #{boardWriter}

mapper.insert:
insert
  into reply
  (
    reply_no
  , board_no
  , reply_writer
  , reply_content
  )
  values
  (
    SEQ_RPL.NEXTVAL
  , 'B' || #{boardNo}
  , #{replyWriter}
  , #{replyContent}
  )

mapper.update:
update
       reply
   set status = 'N'
 where reply_no = #{replyNo}
   and board_no = #{boardNo}
   and reply_writer = #{replyWriter}

update
       reply
   set status = 'N'
 where reply_no = #{replyNo}
   and board_no = 'B' || #{boardNo}
   and reply_writer = #{replyWriter}

mapper.insert:
insert
  into likes
  (
    board_no
  , user_no
  )
values 
  (
  	'B' || #{boardNo}
  , #{userNo}	
  )

mapper.delete:
delete
  from likes
 where board_no = 'B' || #{boardNo}
   and user_no = #{userNo}

mapper.select:
select
  	   count(*)
  from likes
 where board_no = 'B' || #{boardNo}
   and user_no = #{userNo}

select board_no , user_id as "board_writer" , board_title , create_date , count , board_type , b.status , (select count(*) from attachment where ref_no = board_no) as "fileNo" from board b join member on (user_no = board_writer)
where board_type = #{type}
order by to_number(substr(board_no, 2)) desc
