mapper.insert:
  		insert 
  		  into product
		  (
		    product_no
		  , category_no
		  , product_name
		  , price
		  , sale
		  , delivery
		  , thumbnail
		  , product_option
		  )
		  values
		  (
		  	'P' || seq_pno.nextval
		  , #{categoryNo}
		  , #{productName}
		  , #{price} 
		  , #{sale} / 100
		  , #{delivery}
		  , #{thumbnail}
		  , #{productOption}
		  )

insert
  into options
  (
    product_no
  , option_name
  , price
  )
  values
  (
    'P' || seq_pno.currval
  , #{optionName}
  , #{price}
  )
