mapper.update:
update
	   question
   set q_title = #{qtitle}
   	 , q_category = #{qcategory}
     , q_content = #{qcontent}
 where q_no = 'Q' || #{qno}

update
	   attachment
   set origin_name = #{originName}
     , change_name = #{changeName}
     , file_path = #{filePath}
 where ref_no = #{refNo}

mapper.insert:
insert
  into answer
  (
    a_no
  , q_no
  , a_content
  )
  values
  (
    SEQ_ANO.NEXTVAL
  , 'Q' || #{qno}
  , #{acontent}
  )

mapper.update:
update
       answer
   set a_status = 'D'
 where a_no = #{ano}
   and q_no = 'Q' || #{qno}
